mapper.select:
SELECT B.* FROM( SELECT A.*, ROW_NUMBER() OVER(ORDER BY A.BF_NO DESC) RNUM FROM( SELECT BF_NO , BF_TITLE , BF_CONTENT , BF_REGDATE , (SELECT USER_NAME FROM "USER" WHERE USER_NO = BOARD_FORM.USER_NO) AS bfWriter , BF_DELYN , BT_NO FROM BOARD_FORM WHERE BT_NO = 1 AND BF_DELYN = 'N'
ORDER BY BF_NO DESC )A )B
WHERE B.RNUM >= #{startRow} AND B.RNUM <= #{endRow}

mapper.update:
UPDATE
	BOARD_FORM
SET
	BF_TITLE = #{bfTitle}
	,BF_CONTENT = #{bfContent}
	,BF_REGDATE = SYSDATE
WHERE
	BF_NO = #{bfNo}
AND
	BT_NO = 1

mapper.select:
SELECT B.* FROM( SELECT A.*, ROW_NUMBER() OVER(ORDER BY A.BF_NO DESC) RNUM FROM( SELECT BF_NO , BF_TITLE , BF_CONTENT , BF_REGDATE , BT_NO FROM BOARD_FORM WHERE BT_NO = 1
AND BF_DELYN <> 'Y' ORDER BY BF_NO DESC )A )B
WHERE B.RNUM >= #{startRow} AND B.RNUM <= #{endRow}
