mapper.select:
select b.* from ( select a.*, ROWNUM AS rnum from( SELECT DISTINCT O.ORDER_NO, O.ORDER_TOTALPRICE, O.ORDER_PAY, O.ORDER_DATE, OD.OD_NO, OD.OD_QUANTITY, OD.OD_PRICE, OD.OD_QUANTITY * OD.OD_PRICE AS OD_TOTALPRICE, OD.OD_STATUS, G.GOODS_NO, G.GOODS_NAME, G.GOODS_ARTIST, G.GOODS_THUMBNAIL, G.GOODS_PRICE, U.USER_ID FROM "ORDER" O LEFT OUTER JOIN ORDER_DETAIL OD ON (O.ORDER_NO = OD.ORDER_NO) LEFT OUTER JOIN GOODS G ON (G.GOODS_NO = OD.GOODS_NO) LEFT OUTER JOIN "USER" U ON (O.USER_NO = U.USER_NO) WHERE O.ORDER_DELIVERY = #{orderDelivery}
ORDER BY O.ORDER_DATE DESC ) A ) B
WHERE B.RNUM >= #{startRow} AND B.RNUM <= #{endRow}

mapper.update:
UPDATE ORDER_DETAIL
SET OD_STATUS = 2
WHERE OD_NO = #{odNo}

UPDATE ORDER_ISSUE 
SET GUI_CNGDATE = SYSDATE
WHERE ORDER_NO = #{orderNo} AND OD_NO = #{odNo}

mapper.select:
select b.* from ( select a.*, ROWNUM AS rnum from( SELECT OD.ORDER_NO, O.ORDER_TOTALPRICE, O.ORDER_PAY, O.ORDER_DATE, OD.OD_NO, OD.OD_QUANTITY, OD.OD_PRICE, OD.OD_STATUS, OI.GUI_TYPE, OI.GUI_DETAIL, OI.GUI_DATE, OI.GUI_SAVEPATH, OI.GUI_CNGDATE, G.GOODS_NO, G.GOODS_NAME, G.GOODS_ARTIST, G.GOODS_THUMBNAIL, G.GOODS_PRICE, U.USER_ID FROM "ORDER" O LEFT OUTER JOIN ORDER_DETAIL OD ON (O.ORDER_NO = OD.ORDER_NO) LEFT OUTER JOIN ORDER_ISSUE OI ON(OI.OD_NO = OD.OD_NO) LEFT OUTER JOIN GOODS G ON(G.GOODS_NO = OD.GOODS_NO) LEFT OUTER JOIN "USER" U ON(U.USER_NO = O.USER_NO) WHERE 1=1
AND OI.GUI_TYPE = 2 AND OD.OD_STATUS = #{odStatus} ) A ) B
WHERE B.RNUM >= #{startRow} AND B.RNUM <= #{endRow}

select b.* from ( select a.*, ROWNUM AS rnum from( SELECT DISTINCT O.ORDER_NO, O.ORDER_TOTALPRICE, O.ORDER_PAY, O.ORDER_DATE, OD.OD_NO, OD.OD_QUANTITY, OD.OD_QUANTITY * OD.OD_PRICE AS OD_TOTALPRICE, OD.OD_PRICE, OI.GUI_TYPE, OI.GUI_REASON, OI.GUI_DETAIL, OI.GUI_DATE, OD.OD_STATUS, G.GOODS_NO, G.GOODS_NAME, G.GOODS_ARTIST, G.GOODS_THUMBNAIL, G.GOODS_PRICE, U.USER_ID FROM "ORDER" O LEFT OUTER JOIN ORDER_DETAIL OD ON (O.ORDER_NO = OD.ORDER_NO) LEFT OUTER JOIN ORDER_ISSUE OI ON (OI.ORDER_NO = O.ORDER_NO) LEFT OUTER JOIN GOODS G ON (G.GOODS_NO = OD.GOODS_NO) LEFT OUTER JOIN "USER" U ON (O.USER_NO = U.USER_NO) WHERE OI.GUI_TYPE = 1
ORDER BY OI.GUI_DATE DESC ) A ) B
WHERE B.RNUM >= #{startRow} AND B.RNUM <= #{endRow}

mapper.insert:
insert into order_issue(
    order_no, gui_type, gui_detail, od_no, gui_savepath, gui_date
)values(
    #{orderNo}, #{guiType}, #{guiDetail}, #{odNo}, #{guiSavepath}, sysdate
)

mapper.update:
UPDATE GOODS
SET GOODS_QUANTITY = GOODS_QUANTITY + #{odQuantity}
WHERE GOODS_NO = (SELECT GOODS_NO FROM ORDER_DETAIL WHERE OD_NO = #{odNo})

mapper.insert:
insert into order_issue(
    order_no ,od_no, gui_type, gui_reason, gui_detail, gui_date
