mapper.select:
select b.* from ( select a.* , row_number() over (order by a.user_no desc) as rnum from ( select u.user_no, u.user_name, u.user_id, a.artist_fakename from "USER" u left join "ARTIST" a on u.user_no = a.user_no where u.user_type = '3'
) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.insert:
	
		select seq_user.nextval from dual
	
	insert into "USER"(
		user_no
		, user_id
		, user_pw
		, user_name
		, user_profile
		, user_type
		, enabled
	)values(
		#{userNo}
		, #{userId}
		, #{userPw}
		, #{userName}
		, #{userProfile}
		, #{userType}
		, '1'
	)

mapper.select:
select b.* from( select a.* , row_number() over(order by report_no desc) rnum from( select r.report_no , report_gubun , report_date , report_type , report_reason , f.feed_content , f.feed_no , f.user_no , user_id from report r , feed f , "USER" u where r.feed_no = f.feed_no and f.user_no = u.user_no
)a )b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}
